mapper.select:
-- 查询菜单code SELECT menu_code FROM sys_menu menu INNER JOIN sys_power_menu pm on menu.menu_id = pm.menu_id WHERE pow_id in (
-- 查询权限id SELECT power.pow_id FROM sys_power power INNER JOIN sys_role_power rp on power.pow_id = rp.pow_id WHERE rp.role_id in ( -- 查询角色id SELECT DISTINCT role.role_id AS role_id FROM sys_user_info user INNER JOIN sys_user_role ur on user.user_id = ur.user_id INNER JOIN sys_role role on ur.role_id = role.role_id WHERE role.del_flag = 0 and role.role_status = '0' and user.user_id = #{userId} UNION ALL SELECT DISTINCT role.role_id AS role_id FROM sys_user_group ug INNER JOIN sys_user_group_role ugr on ug.user_group_id = ugr.user_group_id INNER JOIN sys_role role on ugr.role_id = role.role_id WHERE role.del_flag = 0 and role.role_status = '0' and ug.user_group_id in (SELECT DISTINCT gr.user_group_id AS userGroupId FROM sys_user_info user INNER JOIN sys_user_info_group uig ON user.user_id = uig.user_id INNER JOIN sys_user_group gr ON uig.user_group_id = gr.user_group_id WHERE gr.del_flag = 0 and user.user_id = #{userId})
) and pow_type = '1' and power.del_flag = 0 ) and disabled_status = '0' and menu.del_flag = 0

-- 查询菜单code SELECT module_code FROM sys_module_resources module INNER JOIN sys_power_module pm on module.module_id = pm.module_id WHERE pm.pow_id in (
-- 查询权限id SELECT power.pow_id FROM sys_power power INNER JOIN sys_role_power rp on power.pow_id = rp.pow_id WHERE rp.role_id in ( -- 查询角色id SELECT DISTINCT role.role_id AS role_id FROM sys_user_info user INNER JOIN sys_user_role ur on user.user_id = ur.user_id INNER JOIN sys_role role on ur.role_id = role.role_id WHERE role.del_flag = 0 and role.role_status = '0' and user.user_id = #{userId} UNION ALL SELECT DISTINCT role.role_id AS role_id FROM sys_user_group ug INNER JOIN sys_user_group_role ugr on ug.user_group_id = ugr.user_group_id INNER JOIN sys_role role on ugr.role_id = role.role_id WHERE role.del_flag = 0 and role.role_status = '0' and ug.user_group_id in (SELECT DISTINCT gr.user_group_id AS userGroupId FROM sys_user_info user INNER JOIN sys_user_info_group uig ON user.user_id = uig.user_id INNER JOIN sys_user_group gr ON uig.user_group_id = gr.user_group_id WHERE gr.del_flag = 0 and user.user_id = #{userId})
) and pow_type = '0' and power.del_flag = 0 ) and module_code IS NOT null and module_status = '0' and module.del_flag = 0
